mapper.insert:
INSERT INTO MEMBERS (
                        MEM_ID
                        , MEM_PWD
                        , MEM_NAME
                        , MEM_AGE
                        , MEM_GENDER
                        , PHONE
                        , EMAIL
                        , MEM_POINT
                    )
    VALUES (
            #{ id }
            , #{ pwd }
            , #{ name }
            , #{ age }
            , #{ gender }
            , #{ phone }
            , #{ email }
            , #{ point }
            )
